#!/bin/bash
# Admin Integration Tests
# Generated by curd command

BASE_URL=${BASE_URL:-http://localhost:8080}
TABLE="admin"

echo "============================================================"
echo "Admin Integration Tests"
echo "============================================================"
echo "Base URL: $BASE_URL"
echo ""

passed=0
failed=0

test_api() {
    local method=$1
    local endpoint=$2
    local data=$3
    local name=$4

    echo "Test: $name"
    if [ -n "$data" ]; then
        response=$(curl -s -X $method "$BASE_URL$endpoint" -H "Content-Type: application/json" -d '"$data')
    else
        response=$(curl -s -X $method "$BASE_URL$endpoint")
    fi

    if echo "$response" | grep -q "success"; then
        echo "✓ PASS: $name"
        ((passed++))
    else
        echo "✗ FAIL: $name"
        echo "  Response: $response"
        ((failed++))
    fi
    echo ""
}

echo "Test: List $TABLE"
test_api "GET" "/admin/list" "" "List admin"

echo "Test: Create $TABLE"
test_api "POST" "/admin/create" '{"username":"test_1770270714","phone":"0714","role_id":1}' "Create admin"

echo "Test: Detail $TABLE"
test_api "GET" "/admin/detail?id=1" "" "Detail admin"

echo "Test: Update $TABLE"
test_api "POST" "/admin/update" '{"id":1,"username":"updated"}' "Update admin"

echo "Test: Delete $TABLE"
test_api "POST" "/admin/delete" '{"id":1}' "Delete admin"

echo "============================================================"
echo "Test Results"
echo "============================================================"
echo "Passed: $passed"
echo "Failed: $failed"
echo ""

if [ $failed -gt 0 ]; then
    echo "Some tests failed!"
    exit 1
else
    echo "All tests passed!"
    exit 0
fi
